SELECT m.data_time as datatime, m.sido_name as sidoname, m.station_name as stationname, m.mang_name as mangname, m.so2_value as so2value FROM ( SELECT data_time, sido_name, station_name, mang_name, so2_value, row_number() over (partition by sido_name, station_name, mang_name order by data_time desc) rn FROM public.measure_info_real WHERE 1 = 1 ) m WHERE m.rn = 1 and 1 = 1;
